import React from 'react';
import Rating from '@mui/material/Rating';
import StarIcon from '@mui/icons-material/Star';
import { Provider, LikeButton } from "@lyket/react";

const CommentsList = ({ groupName, commentsData }) => {
    // Initialize a state to hold likes for each comment
    const [likes, setLikes] = useState(commentsData.map(() => 0));

    const handleLike = (index) => {
      const newLikes = [...likes];
      newLikes[index] += 1; // Increment the like count
      setLikes(newLikes); // Update the state
    };

    return (
      <div className="comments-section">
        <h2>{groupName}</h2>
        {commentsData.map((comment, index) => (
          <div key={index} className="comment-item">
            <Rating
              name={`read-only-${index}`}
              value={comment.rating}
              readOnly
              precision={0.5}
              emptyIcon={<StarIcon style={{ opacity: 0.55 }} fontSize="inherit" />}
            />
            <p className="comment-text">{comment.text}</p>
            <p className="stakeholder-type">Stakeholder: {comment.stakeholder}</p>
            <button onClick={() => handleLike(index)} className="like-button">
              ❤️ {likes[index]}
            </button>
          </div>
        ))}
      </div>
    );
  };

export default CommentsList;
