import React, { useState, useEffect } from "react";
import pretzelLogo from '../images/pretzel.png';


function Navbar() {
    const [sticky, setSticky] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
            setSticky(window.scrollY > 200);
        };
        window.addEventListener("scroll", handleScroll);
        return () => window.removeEventListener("scroll", handleScroll);
    });

    return (
        <nav className={`${sticky ? 'sticky' : ''}`}>

            <div className="nav-inner d-flex justify-content-between align-items-center">
                <div className="lcontainer d-flex flex-column align-items-center">
                    <img src={pretzelLogo} alt="Pretzel Logo" className="navbar-brand mb-1" style={{ width: '50px',textAlign:"center"}}/>
                    <span className="logo-text">Group A</span>
                </div>
                <div className="links">
                    <a href="#" >Main</a>
                    <a href="#" >Introduction</a>
                    <a href="#" >Comment</a>
                    <a href="#" >Vote</a>
                    <a href="#" >Contacts</a>
                </div>
            </div>
        </nav>
    )


}
export default Navbar;
