import React, { useState } from 'react';
import './VotingComponent.css';
import image_test from '../images/image_test.png';



const imageData = [
    { id: 'A', label: image_test, votes: 0 },
    { id: 'B', label: 'Rendering Image B', votes: 0 },
    { id: 'C', label: 'Rendering Image C', votes: 0 },
    { id: 'D', label: 'Rendering Image D', votes: 0 },
    { id: 'E', label: 'Rendering Image E', votes: 0 }
];

const VotingComponent = () => {
    const [images, setImages] = useState(imageData);

    const handleVote = (id) => {
        const updatedImages = images.map((image) => {
            if (image.id === id) {
                return { ...image, votes: image.votes + 1 };
            }
            return image;
        });
        setImages(updatedImages);
    };

    return (
        <div className="voting-container">
            <h1>Vote</h1>
            <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry...</p>
            <div className="images-grid">
                {images.map((image) => (
                    <div key={image.id} className="image-item">
                        <div className="image-placeholder">{image.label}</div>
                        <img src={image_test} alt="Intro Overview"  />

                        <button className
                            ="vote-button" onClick={() => handleVote(image.id)}>
                            Vote
                        </button>
                        <span className="votes-count">{image.votes}</span>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default VotingComponent;
