        rating: 4,

        count: 7,
        stakeholder: 'Local Residents',
        groupId: "A",
      }, {
        component: "Road",
        text: 'I like the design.',
        rating: 4,

        count: 7,
        stakeholder: 'Local Residents', groupId: "A",

      }, {
        component: "Road",
        text: 'I like the design.',
        rating: 4,

        count: 7,
        stakeholder: 'Local Residents', groupId: "A",
      },
    ],
    "Structure": [
      {
        component: "Structure",
        text: 'Too short.',
        rating: 3,

        count: 7,
        stakeholder: 'Commuter', groupId: "A",
      },
      {
        component: "Structure",
        text: 'I like the design.',
        rating: 5,

        count: 7,
        stakeholder: 'Local Residents', groupId: "A",
      },
      {
        component: "Structure",
        text: 'I like the design.',
        rating: 5,

        count: 7,
        stakeholder: 'Local Residents', groupId: "A",
      }, {
        component: "Structure",
