import React, { useState } from 'react';
import Rating from '@mui/material/Rating';
import StarIcon from '@mui/icons-material/Star';
import FavoriteIcon from '@mui/icons-material/Favorite';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';

const CommentsList = ({ groupName, comments }) => {
  // 初始化每个评论的点赞数为0
  const [likes, setLikes] = useState(comments.map(() => 0));

  // 点击心形图标时的处理函数
  const handleLike = (index) => {
    const newLikes = [...likes];
    newLikes[index] += 1; // 增加当前评论的点赞数
    setLikes(newLikes);
  };

  return (
    <div className="comments-section">
      <h2>{groupName}</h2>
      {comments.map((comment, index) => (
        <div key={index} className="comment-item">
          <div className="comment-content">
            <Rating
              name={`read-only-${index}`}
              value={comment.rating}
              readOnly
              precision={0.5}
              emptyIcon={<StarIcon style={{ opacity: 0.55 }} fontSize="inherit" />}
            />
            <p className="comment-text">{comment.text}</p>
            <p className="stakeholder-type">Stakeholder: {comment.stakeholder}</p>
          </div>
          <div className="like-section" onClick={() => handleLike(index)} style={{ cursor: 'pointer' }}>
            {likes[index] > 0 ? <FavoriteIcon color="secondary" /> : <FavoriteBorderIcon />}
            <span className="like-count">{likes[index]}</span>
          </div>
        </div>
      ))}
    </div>
  );
};

export default CommentsList;
