import React, { useState, useEffect } from 'react';
import Rating from '@mui/material/Rating';
import StarIcon from '@mui/icons-material/Star';

const CommentsList = () => {
  const [componentsComments, setComponentsComments] = useState([]);

  useEffect(() => {
    // Fetch comments from the API and group them by component
    fetch('/api/comments') // Replace with your actual API endpoint
      .then((response) => response.json())
      .then((data) => setComponentsComments(data))
      .catch((error) => console.error('Error fetching comments:', error));
  }, []);

  return (
    <div>  {componentsComments.map((component) => (
        <div key={component.name} className="component-section">
          <h2 className="component-heading">{component.name}</h2>
          {component.comments.map((comment, index) => (
            <div key={index} className="comment-block">
              <Rating
                name={`read-only-${index}`}
                value={comment.rating}
                readOnly
                precision={0.5}
                emptyIcon={<StarIcon style={{ opacity: 0.55 }} fontSize="inherit" />}
              />
              <p className="comment-text">{comment.text}</p>
              <p className="stakeholder-info">Stakeholder: {comment.stakeholder}</p>
            </div>
          ))}
        </div>
      ))}
    </div>
);
};

export default CommentsDisplay;
