import React, { useState } from 'react';
import './comment.css';
import Box from '@mui/material/Box';
import Rating from '@mui/material/Rating';
import StarIcon from '@mui/icons-material/Star';

const labels = {
  0.5: 'Useless',
  1: 'Useless+',
  1.5: 'Poor',
  2: 'Poor+',
  2.5: 'Ok',
  3: 'Ok+',
  3.5: 'Good',
  4: 'Good+',
  4.5: 'Excellent',
  5: 'Excellent+',
};
function getLabelText(value) {
  return `${value} Star${value !== 1 ? 's' : ''}, ${labels[value]}`;
}



const CommentModal = ({ submitComment }) => {

  const [value, setValue] = useState(2);
  const [hover, setHover] = useState(-1);
  const [comment, setComment] = useState('');
  const [stakeholder, setStakeholder] = useState('');
  const stakeholderOptions = [
    "Government and Planning Authorities",
    "Local Residents",
    "Transportation Departments",
    "Construction and Engineering Firms",
    "Environmental Groups",
    "Financial and Investment Parties",
    "Public Safety and Health Departments",
    "Commuter",
    "Other",
  ];



  const handleInputChange = (event) => {
    setComment(event.target.value);
  };

  const handleRatingChange = (newRating) => {
    setRating(newRating);
  };

  const handleStakeholderChange = (event) => {
    setStakeholder(event.target.value);
    };

    const handleFormSubmit = (event) => {
    event.preventDefault();
    submitComment({ comment, rating, stakeholder });
    };

    return (
    <div className="comment-modal">
    <div className="modal-content">
    <form onSubmit={handleFormSubmit}>
    <Box
sx={{
width: 200,
display: 'flex',
alignItems: 'center',
}}
>
<Rating
name="hover-feedback"
value={value}
precision={0.5}
getLabelText={getLabelText}
onChange={(event, newValue) => {
setValue(newValue);
}}
onChangeActive={(event, newHover) => {
setHover(newHover);
}}
emptyIcon={<StarIcon style={{ opacity: 0.55 }} fontSize="inherit" />}
/>
{value !== null && (
<Box sx={{ ml: 2 }}>{labels[hover !== -1 ? hover : value]}</Box>
)}
</Box>



    <textarea
             value={comment}
             onChange={handleInputChange}
             placeholder="Leave your comments"
             className="comment-input"
           />


    <div className="stakeholder-select">
    <p>Whatâ€™s your type of stakeholders?</p>

    {stakeholderOptions.map((option) => (
          <button
            key={option}
            className={`stakeholder-button ${stakeholder === option ? "active" : ""}`}
            onClick={() => setStakeholder(option)}
          >
            {option}
          </button>
        ))}


    </div>
    <div className="form-actions">
    <button type="button" className="cancel-button">CANCEL</button>
    <button type="submit" className="submit-comment">CONFIRM</button>
    </div>
    </form>
    </div>
    </div>
    );
    };

    export default CommentModal;
