import React,{useState,useEffect} from "react";

const HeaderButton = ({ children, href }) => {
    return (
      <a className="HeaderButton" href={href}>
        {children}
      </a>
    );
  };

function Navbar(){
    const [sticky,setSticky]=useState(false);

    useEffect(()=>{
        const handleScroll=()=>{
            setSticky(window.scrollY>200);
        };
        window.addEventListener("scroll",handleScroll);
        return ()=>window.removeEventListener("scroll",handleScroll);
    });

    return(
        <nav className={`Header ${sticky ? 'sticky' : ''}`}>
            <div className="nav-inner">
                <span className="logo">
                    Group A
                </span>
                <div className="links">
                    <HeaderButton heref="#">Main</HeaderButton>
                    <a heref="#">Introduction</a>
                    <a heref="#">Comment</a>
                    <a heref="#">Vote</a>
                    <a heref="#">Contacts</a>
                </div>
            </div>
        </nav>
    )


}
export default Navbar;
