import React, { useState } from 'react';


const CommentComponent = () => {
  const [currentRating, setCurrentRating] = useState(0);
  const [currentComment, setCurrentComment] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [comments, setComments] = useState([]);

  const handleRating = (rating) => {
    setCurrentRating(rating);
    setShowModal(true);
  };

  const handleCommentChange = (event) => {
    setCurrentComment(event.target.value);
  };

  const submitComment = () => {
    const newComment = {
      rating: currentRating,
      text: currentComment,
      // Additional data can be added here like user info or timestamps
    };
    setComments([...comments, newComment]);
    setShowModal(false);
    setCurrentRating(0);
    setCurrentComment('');
  };

  return (
    <div className='comment-container'>
      <div className='rating-buttons'>
        {[1, 2, 3, 4, 5].map((number) => (
          <button key={number} onClick={() => handleRating(number)}>
            {number} Stars
          </button>
        ))}
      </div>

      {showModal && (
        <div className='modal'>
          <div className='modal-content'>
            <div className='rating-display'>{currentRating} Stars</div>
            <textarea
              value={currentComment}
              onChange={handleCommentChange}
            ></textarea>
            <div className='stakeholder-buttons'>
              {/* Stakeholder buttons can be implemented here */}
            </div>
            <button onClick={() => setShowModal(false)}>Cancel</button>
            <button onClick={submitComment}>Confirm</button>
          </div>
        </div>
      )}

      <div className='comments-display'>
        {comments.map((comment, index) => (
          <div key={index} className='comment'>
            <div className='comment-rating'>{comment.rating} Stars</div>
            <p className='comment-text'>{comment.text}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CommentComponent;
