import React,{useState,useEffect} from "react";

function Navbar(){
    const [sticky,setSticky]=useState(false);

    useEffect(()=>{
        const handleScroll=()=>{
            setSticky(window.scrollY>200);
        };
        window.addEventListener("scroll",handleScroll);
        return ()=>window.removeEventListener("scroll",handleScroll);
    });

    return(
        <nav className={`${sticky ? 'sticky' : ''}`}>
            <div className="Rectangle50" style={{width: 1440, height: 112, left: 0, top: 0, position: 'absolute', background: '#0065BD'}} />

            <div className="nav-inner">
                <span className="logo">
                    Group A
                </span>
                <div className="links">
                    <a heref="#">Main</a>
                    <a heref="#">Introduction</a>
                    <a heref="#">Comment</a>
                    <a heref="#">Vote</a>
                    <a heref="#">Contacts</a>
                </div>
            </div>
        </nav>
    )


}
export default Navbar;
