import React from 'react';
import intro_overview from '../images/intro_overview.png';
import driver_side from '../images/Picture-4.png'; // 确保图片的路径是正确的

function Introduction() {
  return (
    <div>
      {/* Container for the first row with background */}
      <div className="container-fluid" style={{ backgroundColor: '#FBFBFB', paddingBottom: '50px' }}>
        <div className="row">
          {/* Left column for the first image */}
          <div className="col-lg-6 col-md-12" style={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
            <img src={intro_overview} alt="Bridge" style={{ maxWidth: '100%', height: 'auto' }} />
          </div>
          {/* Right column for the first text */}
          <div className="col-lg-6 col-md-12" style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', padding: '0 20px' }}>
            <h1 style={{ color: '#333', fontSize: '48px', fontFamily: 'Roboto', fontWeight: 'bold' }}>Introduction</h1>
            <p style={{ color: '#666', fontSize: '18px', fontFamily: 'Roboto', fontWeight: 'normal', lineHeight: '1.6' }}>
              Location:<br />
              Year:<br />
              Author<br />
              Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book.
            </p>
            <button style={{ fontSize: '18px', fontWeight: 'bold', color: '#0065BD', backgroundColor: 'transparent', border: 'none', cursor: 'pointer' }}>
              READ MORE →
            </button>
          </div>
        </div>
      </div>
      {/* Container for the second row without background */}
      <div className="container-fluid">
        <div className="row">
          {/* Left column for the second image */}
          <div className="col-lg-6 col-md-12" style={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
            <img src={driver_side} alt="Side view of a bus" style={{ maxWidth: '100%', height: 'auto' }} />
          </div>
          {/* Right column for the second text */}
          <div className="col-lg-6 col-md-12" style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', padding: '0 20px' }}>
            <h2 style={{ color: '#333', fontSize: '36px', fontFamily: 'Roboto', fontWeight: 'bold' }}>Second Section Title</h2>
            <p style={{ color: '#666', fontSize: '18px', fontFamily: 'Roboto', fontWeight: 'normal', lineHeight: '1.6' }}>
              Additional information about the second section goes here. You can fill in any text or details that pertain to the second image.
            </p>
            {/* Optionally, you can add another button or any other elements here */}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Introduction;
