import './App.css';
import './index.css';
import Navbar from './components/Navbar'
import Footer from './components/Footer'
import Home from './components/Home'
import Introduction from './components/Introduction'
import ModelViewer from './components/model_viewer'
import CommentList from './components/CommentList'
import React, { useState } from 'react';

function App() {
  const [selectedComponent, setSelectedComponent] = useState(''); // State to track selected component

  const [groupedComments] = useState({
    "Roads": [
      {
        text: 'Good job!',
        rating: 5,
        stakeholder: 'Commuter'
      },
      // Add more comments as needed
    ],
    "Structure": [
      {
        text: 'Too short.',
        rating: 3,
        stakeholder: 'Commuter'
      },
      // Add more comments as needed
    ],
    // Add more components and comments as needed
  });

  // Function to handle the change in the selected component
  const handleComponentChange = (componentName) => {
    setSelectedComponent(componentName);
  };

  return (
    <div className="App">
      <Navbar />
      <Home />
      <Introduction />
      <ModelViewer />

      {/* Render buttons to select the component */}
      {Object.keys(groupedComments).map((componentName) => (
        <button key={componentName} onClick={() => handleComponentChange(componentName)}>
          Select {componentName}
        </button>
      ))}

      {/* Render the selected component's comments */}
      {selectedComponent && (
        <CommentList groupName={selectedComponent} comments={groupedComments[selectedComponent]} />
      )}

      <Footer />
    </div>
  );
}

export default App;
