import React from 'react';
import Rating from '@mui/material/Rating';
import StarIcon from '@mui/icons-material/Star';
import { Provider, LikeButton } from "@lyket/react";

const CommentsList = ({ groupName, comments }) => {
  return (
    <Provider apiKey="pt_1de51c622b373fec9690e40860d58e"> {/* Replace with your actual Lyket API key */}
      <div className="comments-section">
        <h2>{groupName}</h2>
        {comments.map((comment, index) => (
          <div key={index} className="comment-item">
            <div className="rating-row">
              <Rating
                name={`read-only-${index}`}
                value={comment.rating}
                readOnly
                precision={0.5}
                emptyIcon={<StarIcon style={{ opacity: 0.55 }} fontSize="inherit" />}
              />
            </div>
            <div className="comment-row">
              <p className="comment-text">{comment.text}</p>
              <LikeButton
                namespace="comments"
                id={`comment-like-${index}`} // Unique ID for each comment
                component={LikeButton.templates.Twitter}
              />
            </div>
            <p className="stakeholder-row">Stakeholder:
            {comment.stakeholder}</p>
</div>
))}
</div>
</Provider>
);
};

export default CommentsList;
