import React, { useState } from 'react';
import './VotingComponent.css';
import image_test from '../images/image_test.png';
import { BarChart } from '@mui/x-charts/BarChart';

const imageData = [
    { id: 'A', src: image_test, votes: 0 },
    { id: 'B', src: image_test, votes: 0 },
    { id: 'C', src: image_test, votes: 0 },
    { id: 'D', src: image_test, votes: 0 },
    { id: 'E', src: image_test, votes: 0 }
];

const VotingComponent = () => {
    const [images, setImages] = useState(imageData);

    const handleVote = (id) => {
        const updatedImages = images.map((image) => {
            if (image.id === id) {
                return { ...image, votes: image.votes + 1 };
            }
            return image;
        });
        setImages(updatedImages);
    };

    const chartData = {
        labels: images.map(image => image.id),
        datasets: [
            {
                label: 'Votes',
                data: images.map(image => image.votes),
                backgroundColor: 'rgba(0, 123, 255, 0.5)',
            },
        ],
    };

    return (
        <div className="voting-container">
            <h1 style={{ textAlign: 'left', marginLeft: '40px' }}>Vote</h1>
            <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry...</p>
            <div className="images-grid">
                {images.map((image) => (
                    <div key={image.id} className="image-item">
                        <img src={image.src} alt={`Rendering Image ${image.id}`} className="image" />
                        <div className="vote-section">
                            <button className="ifc-model-button" onClick={() => handleVote(image.id)}>
                                IFC MODEL
                            </button>
                            <button className="vote-button" onClick={() => handleVote(image.id)}>
                                Vote
                            </button>
                        </div>
                    </div>
                ))}
            </div>
            <div style={{ height: 300, width: '100%' }}>
                <BarChart data={chartData} />
            </div>
        </div>
    );
};

export default VotingComponent;
