import React,{useState,useEffect} from "react";

function Navbar(){
    const [sticky,setSticky]=useState(false);

    useEffect(()=>{
        const handleScroll=()=>{
            setSticky(window.scrollY>200);
        };
        window.addEventListener("scroll",handleScroll);
        return ()=>window.removeEventListener("scroll",handleScroll);
    });

    return(
        <nav className={`Header ${sticky ? 'sticky' : ''}`} style={{width: 1440, height: 112, position: 'relative'}}>
      {/* Background Rectangle */}
      <div className="Rectangle50" style={{width: 1440, height: 112, left: 0, top: 0, position: 'absolute', background: '#0065BD'}} />

      {/* Logo and Image */}
      <div className="GroupA" style={{left: 90, top: 68, position: 'absolute', color: 'white', fontSize: 32, fontFamily: 'Newsreader', fontWeight: '500', lineHeight: '32px', wordWrap: 'break-word'}}>
        Group A
        <img className="Image2" style={{width: 61, height: 48.85, position: 'absolute'}} src="https://via.placeholder.com/61x49" alt="Logo" />
      </div>

      {/* Navigation Links */}
      <div className="links" style={{position: 'absolute', top: 33, display: 'flex', alignItems: 'center', gap: '10px'}}>
        <a className="HeaderButton" href="#" style={linkStyle}>Main</a>
