import React, { useState } from 'react';
import CommentModal from './Comment';
import CommentList from './CommentList'; // Adjust the import path as needed
import './model_viewer.css';

function ModelViewer() {
  const [selectedComponent, setSelectedComponent] = useState('Overrall'); // Set to 'Overrall' as the default
  const [comments, setComments] = useState([]); // Initialize comments as an array
  const [isModalOpen, setIsModalOpen] = useState(false);

  const componentsList = ['Overrall', 'Pedestrian Space', 'Road', 'Access to public transport', 'Structure'];

  const handleDropdownChange = (event) => {
    const newSelectedComponent = event.target.value;
    setSelectedComponent(newSelectedComponent);

    // Assuming you have a function to fetch comments based on the selected component
    // You can call this function here and update the comments state accordingly
    fetchComments(newSelectedComponent);
  };

  const fetchComments = (selectedComponent) => {
    // You should fetch comments based on the selectedComponent
    // For now, let's assume you have the comments fetched from an API and stored in an array
    const fetchedComments = getCommentsByComponent(selectedComponent);
    setComments(fetchedComments);
  };

  const submitComment = (comment) => {
    // Update comments as an array of objects
    setComments([...comments, { component: selectedComponent, text: comment, rating: 0, stakeholder: '' }]);
    setIsModalOpen(false);
  };

  const handleCommentClick = () => {
    setIsModalOpen(true);
  };

  return (
    <div className="model-viewer">
      <div className="model-viewer-header">
        <h1>Model Viewer</h1>
      </div>

      <div className="model-display">
        {selectedComponent ? selectedComponent : 'New IFC Modeling'}
      </div>

      <div className="components-section">
        <p className="components-label">Components</p>
        <select 
          value={selectedComponent} 
          onChange={handleDropdownChange}
          className="component-dropdown"
        >
          {componentsList.map((component) => (
            <option key={component} value={component}>{component}</option>
          ))}
        </select>
        <button className="comment-button" onClick={handleCommentClick}>COMMENT</button>
      </div>

      {isModalOpen && (
        <CommentModal
          selectedComponent={selectedComponent}
          submitComment={submitComment}
        />
      )}

      {/* Render the CommentsList component with the selected component and comments */}
      <CommentList selectedComponent={selectedComponent} comments={comments} />
    </div>
  );
}

export default ModelViewer;
