import React, { useState } from 'react';
import Rating from '@mui/material/Rating';
import StarIcon from '@mui/icons-material/Star';
import FavoriteIcon from '@mui/icons-material/Favorite'; // Import for heart icon
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder'; // Import for outlined heart icon

const CommentsList = ({ groupName, comments }) => {
  // Create a state to manage likes for each comment
  const [likes, setLikes] = useState(comments.map(() => false));

  // Function to toggle like state
  const toggleLike = (index) => {
    const newLikes = [...likes];
    newLikes[index] = !newLikes[index];
    setLikes(newLikes);
  };

  return (
    <div className="comments-section">
      <h2>{groupName}</h2>
      {comments.map((comment, index) => (
        <div key={index} className="comment-item">
          <div className="comment-content">
            <Rating
              name={`read-only-${index}`}
              value={comment.rating}
              readOnly
              precision={0.5}
              emptyIcon={<StarIcon style={{ opacity: 0.55 }} fontSize="inherit" />}
            />
            <p className="comment-text">{comment.text}</p>
            <p className="stakeholder-type">Stakeholder: {comment.stakeholder}</p>
          </div>
          <div className="like-icon" onClick={() => toggleLike(index)} style={{ cursor: 'pointer' }}>
            {likes[index] ? <FavoriteIcon color="secondary" /> : <FavoriteBorderIcon />}
          </div>
        </div>
      ))}
    </div>
  );
};

export default CommentsList;
